// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./NextItem.sol";
import "./NextPack.sol";

contract NextShop is Ownable, ERC1155Holder {

    using Counters for Counters.Counter;

    struct SaleItem
    {
        address nftContract;
        address erc20Contract;
        uint256 id;
        uint256 tokenId;
        uint256 maximumPurchaseQuantity;
        uint256 quantity;
        uint256 itemLeft;
        uint256 price;
        uint256 startTime;
        uint256 endTime;
    }

    Counters.Counter private _itemIds;
    mapping(uint256 => SaleItem) private items;

    address nextverseWallet;

    event ItemAdded(
        address indexed sender,
        SaleItem item
    );

    event ItemClosed(
        address indexed sender,
        uint256 id,
        uint256 itemLeft
    );

    event ItemPurchased(
        address indexed sender,
        uint256 id,
        uint256 tokenId,
        uint256 quantity,
        uint256 price,
        address nftContract,
        address erc20Contract
    );

    event ItemPriceUpdated(
        address indexed sender,
        uint256 id,
        uint256 newPrice,
        address erc20Contract
    );

    event SellingTimeUpdated(
        address indexed sender,
        uint256 id, 
        uint256 startTime,
        uint256 endTime
    );

    constructor(address ownerWallet) {
        nextverseWallet = ownerWallet;
    }

    modifier isValidItemId(uint256 itemId) {
        require(itemId < _itemIds.current(), "Invalid itemId");
        _;
    }

    modifier isValidTokenId(uint256 tokenId, address nftContract) {
        INFTFactory nftFactory = INFTFactory(nftContract);
        require(tokenId < nftFactory.getUniqueTokenNum(), "Invalid tokenId");
        _;
    }

    function addItemForSale(SaleItem memory item)
        public
        onlyOwner
        isValidTokenId(item.tokenId, item.nftContract)
    {
        require(item.price > 0, "Price must be greater than 0");
        require(item.quantity > 0, "quantity must be greater than 0");

        IERC1155 nftFactory = IERC1155(item.nftContract);
        nftFactory.safeTransferFrom(owner(), address(this), item.tokenId, item.quantity, "0x0");

        uint256 id = _itemIds.current();
        _itemIds.increment();
        item.id = id;
        items[id] = item;

        emit ItemAdded(msg.sender, item);
    }

    function purchaseItem(uint256 itemId, uint256 quantity)
        public
        isValidItemId(itemId)
    {
        SaleItem storage item = items[itemId];
        uint256 currentTime = block.timestamp;
        require(((currentTime >= item.startTime) && (currentTime < item.endTime)), "Item is not available");
        require(quantity <= item.itemLeft, "insufficient item quantity");
        require((quantity > 0) && (quantity <= item.maximumPurchaseQuantity), "Invalid quantity value");

        IERC20 token = IERC20(item.erc20Contract);
        uint256 paymentAmount = item.price * quantity;
        require(token.balanceOf(msg.sender) >= paymentAmount, "insufficient balance to buy");

        item.itemLeft -= quantity;

        token.transferFrom(msg.sender, nextverseWallet, paymentAmount);

        IERC1155 nftFactory = IERC1155(item.nftContract);
        nftFactory.safeTransferFrom(address(this), msg.sender, item.tokenId, quantity, "0x0");

        emit ItemPurchased(msg.sender, itemId, item.tokenId, quantity, item.price, item.nftContract, item.erc20Contract);
    }

    function updatePrice(uint256 itemId, uint256 price, address erc20Contract)
        public
        onlyOwner
        isValidItemId(itemId)
    {
        SaleItem storage item = items[itemId];
        require(item.startTime > block.timestamp, "Can only be updated for upcoming item");

        items[itemId].price = price;
        items[itemId].erc20Contract = erc20Contract;

        emit ItemPriceUpdated(msg.sender, itemId, price, erc20Contract);
    }

    function updateSellingTime(uint256 itemId, uint256 startTime, uint256 duration)
        public
        onlyOwner
        isValidItemId(itemId)
    {
        require(items[itemId].startTime > block.timestamp, "Can only be updated for upcoming item");
        require(block.timestamp < startTime, "Start time must be greater than current time");

        items[itemId].startTime = startTime;
        items[itemId].endTime = startTime * duration * (1 minutes);

        emit SellingTimeUpdated(msg.sender, itemId, startTime, items[itemId].endTime);
    }

    function closeSaleItem(uint256 itemId)
        public 
        onlyOwner
        isValidItemId(itemId)
    {
        SaleItem storage item = items[itemId];
        uint256 itemLeft = item.itemLeft;
        IERC1155 nftFactory = IERC1155(item.nftContract);
        nftFactory.safeTransferFrom(address(this), owner(), item.tokenId, itemLeft, "0x0");
        item.itemLeft = 0;

        emit ItemClosed(msg.sender, itemId, itemLeft);
    }

    function getSaleItem(uint256 itemId)
        public
        view
        isValidItemId(itemId)
        returns (SaleItem memory)
    {
        return items[itemId];
    }

    function getTotalSaleItems()
        public
        view
        returns (uint256)
    {
        return _itemIds.current();
    }

    function getOnSaleItems()
        public
        view
        returns (SaleItem[] memory)
    {
        uint256 maxIndex = _itemIds.current();
        uint256 currentTime = block.timestamp;

        uint256 cnt = 0;
        for (uint256 i = 0; i < maxIndex; i++) {
            if ((items[i].startTime <= currentTime) && (currentTime  < items[i].endTime)) {
                cnt++;
            }
        }

        SaleItem[] memory ret = new SaleItem[](cnt);
        for (uint256 i = 0; i < maxIndex; i++) {
            if ((items[i].startTime <= currentTime) && (currentTime  < items[i].endTime)) {
                cnt--;
                ret[cnt] = items[i];
            }
        }

        return ret;
    }

    function getUpcomingSaleItems()
        public
        view
        returns (SaleItem[] memory)
    {
        uint256 maxIndex = _itemIds.current();
        uint256 currentTime = block.timestamp;

        uint256 cnt = 0;
        for (uint256 i = 0; i < maxIndex; i++) {
            if (currentTime < items[i].startTime) {
                cnt++;
            }
        }

        SaleItem[] memory ret = new SaleItem[](cnt);
        for (uint256 i = 0; i < maxIndex; i++) {
            if (currentTime < items[i].startTime) {
                cnt--;
                ret[cnt] = items[i];
            }
        }

        return ret;
    }

    function getBlockTimestamp()
        public 
        view 
        returns (uint256 currentTime)
    {
        return block.timestamp;
    }
}
